CREATE OR REPLACE NONEDITIONABLE PROCEDURE insert_RULE
IS
BEGIN
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (1,''Тире между подлежащим и сказуемым ставится, если один из главных членов выражен существительным в именительном падеже (задание), а второй – инфинитивом (неопределенной формой глагола) – разработать, определить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (2,''Деепричастный оборот выделяется с двух сторон запятыми.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (3,''Не погашенную – это причастие, не с причастиями пишется раздельно, если есть еще одно зависимое слово (длительно – зависимое слово (наречие меры) не вызывает раздельного написания, но слово «заемщиком» как еще одно зависимое требует раздельного написания).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (4,''Согласно дорожной картЕ (согласно, благодаря, вопреки согласовываются с Дательным Падежом).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (5,''Запятая в сложноподчиненном предложении ставится перед союзом «что». Запятая закрывает придаточную часть (…. от главной цели).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (6,''Тире ставится перед словом «это».'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (7,''Запятая ставится перед союзом «что», в данном предложении закрывает оборот, перед «и» запятая ставится не всегда. Перед «а» ставится.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (8,''Не шаблонное – это прилагательное, дальше идет противопоставление с союзом а, поэтому пишем раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (9,''Запятая ставится между однородными членами предложения. Не ставится при наличии одинарного союза.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (10,''Тире ставится – если вторая часть бессоюзного предложения указывает на следствие, результат того, о чем говорится в первой. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (11,''Двоеточие ставится - если вторая часть предложения указывает на причину того, о чем говорится в первой. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (12,''Невыполненном – причастие, которое не имеет зависимых слов и пишется слитно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (13,''Вводные слова «во-первых», «во-вторых», указывают на порядок следования мыслей, последовательность изложения выделяется запятыми.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (14,''Частицу «не» следующую за местоимениями, которые начинаются с приставки «ни» (никем, никто) нужно писать раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (15,''Не с причастиями пишется раздельно при наличии пояснительного слова (абсолютно).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (16,''Следует просто запомнить: компания – сообщество лиц, предприятий; кампания – мероприятие, событие.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (17,''Союзы: чтобы, причём, притом, тоже, также и т.д. - пишутся слитно. Местоимения + частица бы, местоимение и наречие + частица же, предлог + местоимение (при том, при чём, за то, от того..)  пишутся раздельно. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (18,''«Не» с причастием пишется раздельно если в предложении есть слова, усиливающие отрицание (двойное отрицание) – «никем».'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (20,''Запятая ставится между однородными членами предложения. Не ставится при наличии одинарного союза.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (21,''«При этом» выполняет роль союза, соединяя два простых предложения. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (22,''Написание одной или двух согласных в словах иноязычного происхождения определяется в словарном порядке, нужно просто запомнить (алюминиевый от производящего - алюминий произошло от латинского слова alumen).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (23,''Запятая ставится между частями сложного предложения, соединенным союзом (и, да, ни…ни, тоже, также), разделительным союзом (или, иль, либо, то…то, не то…не то), противопоставительными союзом (а, но, да, однако, зато, же).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (24,''НЕ пишется слитно с полными формами причастий, если при них нет пояснительных слов.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (25,''НЕ с краткими причастиями пишется раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (27,''Согласно нормативнЫМ документАМ (согласно, благодаря, вопреки согласовываются с Дательным Падежом). Конструкции с «согласно» могут обособляться.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (29,''НЕ с существительными пишется слитно: 1.если слово не употребляется без НЕ; 2.если существительное с НЕ может быть заменено синонимом без НЕ или близким по значению выражением.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (30,''Присоединительные обороты, начинающиеся союзом «в том числе», обособляются. Союз «в том числе» не отделяется запятой от стоящих перед ним союзов «а», «и», образуя с ними единое сочетание.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (26,''После обобщающего слова, перед перечислением ставится двоеточие, а если есть вводное слово (а именно, как то, например, наверное), то двоеточие ставится после него, а перед вводным словом ставится запятая.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (207,''Слово мотивация является словарным.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (208,''Слово интеллект является словарным.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (209,''Слово командировка является словарным.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (28,''В сложноподчиненных предложениях главная и придаточные части отделяются друг от друга запятой, точкой с запятой, тире. В случае, когда придаточное предложение внутри главного, оно выделяется знаками препинания с обеих сторон.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (83,''В суффиксе существительных –ер- после шипящих под ударением пишется е(ё), если существительное образованы от глагола (стажёр-стажировать).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (32,''Вводные слова (наверное, например, думаю..), указывающее на уточнение высказывания, отделяют двумя запятыми (если они стоят в середине предложения) или только одной (если они стоят в начале или конце предложения).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (33,''«Несмотря» пишется слитно как производный предлог. Синоним - хотя. «Не смотря» (частица с деепричастием) пишется раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (34,''При употреблении в качестве вводного слова «как следствие» выделяется запятыми. При употреблении в качестве союза и члена предложения не обособляется.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (35,''При нахождении «вместе с тем» в начале предложения и возможной замены на союзы «но», «тем не менее», «однако», знак препинания ставить не нужно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (36,''Обособляются (отделяются запятой в начале и в конце предложения и выделяются с обеих сторон в середине предложения) слова и словосочетания, уточняющие смысл предшествующих слов.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (37,''Присоединительные обороты, начинающиеся союзом «в том числе», обособляются. Союз «в том числе» не отделяется запятой от стоящих перед ним союзов «а», «и», образуя с ними единое сочетание.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (38,''Обособляются (отделяются запятой в начале и в конце предложения и выделяются с обеих сторон в середине предложения) слова и словосочетания, уточняющие смысл предшествующих слов.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (39,''Две буквы Н пишутся в причастиях, образованных от глаголов совершенного вида и имеющих также зависимые слова.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (40,''Запятая ставится между однородными членами предложения, соединенными при помощи повторяющихся союзов.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (86,''НЕ пишется слитно с прилагательными, если в сочетании с не приобретают противоположное значение, их можно заменить синонимами без не: небольшой (маленький), неженатый (холостой).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (42,''НЕ пишется раздельно с существительными, прилагательными и наречиями если имеется или подразумевается противопоставление с союзом а.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (43,''«Не» с существительным, в котором частица «не» выступает в качестве приставки, меняя значение на противоположное, пишется слитно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (44,''Слова расчет, расчетный - пишутся с одним С, так как образованы от корня чёт. Словарное слово.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (45,''Сочетание при необходимости не является вводным словом. Выделять запятыми его не следует.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (47,''Запятая при союзе как не ставится в части составного союза так как и как…так и'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (48,''Две буквы НН пишутся в прилагательных, образованных от существительных с помощью –ОНН-, -ЕНН-.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (49,''Коммуникации - существительное, пишется с двумя м, слово является иностранным (лат.communicatio - сообщение), поэтому правильность написания следует запомнить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (50,''Для проверки безударной гласной в корне слова подбирается проверочное, в котором сомнительная гласная займет ударную позицию. К глаголу определить невозможно подобрать проверочное слово, поэтому правописание следует запомнить.  '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (51,''Корректный - словарное слово. Образовано от латинского correctio и повторяет его орфографию. Его написание рекомендуется запомнить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (52,''В сложноподчиненном предложении перед союзом который  ставится запятая. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (53,''Наречие, образованное от порядкового числительногос помощью приставок во-, в- и суффикса -ых пишутся через дефис.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (54,''Запятая ставится перед союзом «что».'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (55,''В данном случае слово проверка в родительном падеже (кого?, чего?), а значит окончание -и.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (56,''Запятая перед чтобы ставится, если в сложноподчиненном предложении этот союз отделяет придаточную часть от главной. В сложноподчиненном предложении с одной придаточной частью, находящейся после главного предложения, запятая перед союзом чтобы ставится обязательно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (57,''Слово происходит от латинского слова commercium и повторяет его орфографию. Рекомендуется запомнить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (58,''Перед так как и перед любым подчинительным союзом ставится обязательно запятая, которая отделяет главное предложение от зависимого. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (59,''В данном случае слово ставка в дательном падеже (кому?, чему?), а значит окончание -е.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (60,''Слово пролонгация - словарное. Рекомендуется запомнить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (61,''Придаточные части предложения, присоединяемые союзом «так как», выделяются запятой.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (62,''Проверочное слово должность'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (63,''Обороты с предлогом несмотря обособляются запятыми. Когда оборот стоит в начале предложении, запятая ставится после него, в конце - перед ним, в середине - запятые с двух сторон.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (64,''Не с наречием пишется слитно, если слово не употребляется без не, либо не приставка.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (65,''Корень вед. Правильно писать - приведшее. Проверка:ведет.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (66,''Слово нужно запомнить. Правильно писать - андеррайтера'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (138,''Запятая в сложноподчиненном предложении ставится перед союзом «что».'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (68,''В сложных числительных от 200 до 900 первая часть (от двух до девяти) будет склоняться как простое числительное, вторая часть (р.,д.,тв.,п.падежи) – как существительное 1 склонения во мн.ч (как слово рука).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (69,''Склонение фамилий, оканчивающихся на гласные, не зависит от пола носителей фамилии (Бокерия, Соткилава - склоняются в м.и.ж.р). '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (70,''Числительные полтора (1,5)- полторы во всех падежах, кроме И. и В., имеют форму полутора.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (71,''При склонении количественных числительных изменяются все слова и все части сложных слов, а при склонении порядковых – только последнее слово: пятьсот шестнадцать сотрудников – пятьюстами шестнадцатью сотрудниками (количественное), пятьсот шестнадцатого сотрудника (порядковое).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (72,''В составных числительных склоняется каждое слово. При смешанном числе существительным управляет дробь, а не целое число: сорок пять с половиной процентов, восемьдесят пять целых семь десятых процента, девяносто восемь процентов.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (73,''Географические названия на согласную склоняются (например: Красноярск - в городе Красноярске, Суздаль - в Суздале).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (74,''Географические названия, представляющие собой полные формы прилагательных склоняются вместе с родовым наименованием: на горе Железной, до острова Каменного.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (75,''Мужские и женские фамилии на -о, -е, -и, -у, -ю не склоняются.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (76,''Склоняются фамилии на ударный –а(-я) славянского происхождения, на неударный –а (-я) независимо от происхождения: у Григория Сковороды, стихи Пабло Неруды.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (77,''Склоняются мужские фамилии, совпадающие с именами нарицательными или географическими названиями типа Чуб, Соловей, Рысь: об Александре Рысе.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (78,''Не склоняются русские фамилии на –ых, -их: Алексею Седых, от Нины Черных'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (79,''Не склоняются женские фамилии, совпадающие с именами нарицательными или географическими названиями типа Чуб, Соловей, Рысь: роль Елены Соловей.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (80,''Не склоняются иноязычные и русские фамилии на согласный, если относятся к женщинам: к Марии Френкель, к Елене Волк.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (81,''Склоняются иноязычные и русские фамилии на согласный, если относятся к мужчинам: - к Олегу Френкелю, от Игоря Волка.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (141,''Перед перечислением находится обощающее слово информацию, требует постановки двоеточия.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (152,''Особое склонение имеют все числительные на -сот. (д.п. семистам).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (82,''В окончаниях имен существительных после шипящих под ударением пишется о в соответствии с произношением: метражом, стеллажом, виражом (сравните безударные окончания: пейзажем, престижем, хронометражем, овощем)'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (84,''Сравнительный оборот с союзом как выделяется запятыми в оборотах не что иное как и не кто иной как. В данных сочетаниях пишется НЕ, причем раздельно с последующим местоимением что/кто. Следует отличать от отриц.конструкций (ничто/никто). Никто иной не сможет это сделать.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (153,''Порядковые числительные, оканчивающиеся на -тысячный, -миллионный, -миллиардный пишутся слитно. Возможно, вы написали предложение с маленькой буквы.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (166,''Если лексическое значение слова означает приближение, присоединение, то пишется приставка при-  '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (85,''НЕ пишется слитно с причастиями, прилагательными, наречиями, если при них есть пояснительные слова очень, весьма, крайне, совершенно, почти, абсолютно, чрезвычайно: крайне недоверчивый, совершенно непроверенный факт.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (87,''Пишется слитно (если есть синоним - маленький). Иногда слитное или раздельное написание не с прилагательными определяется возможностью двоякого толкования текста. Дом небольшой (маленький). Дом не большой - отрицается размер.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (88,''НЕ пишется слитно с существительными, прилагательными, наречиями, если при этом образуется новое слово с противоположным значением и возможен подбор синонима без не: неинтересный (скучный), неправда (ложь), неверно (ошибочно).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (89,''НЕ пишется раздельно с причастиями, если: 1. имеют при себе зависимые слова; 2. имеется или подразумевается противопоставление; 3. употреблено в краткой форме и если имеется противопоставление с союзом а. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (90,''НЕ пишется слитно в неопределенных местоимениях и наречиях под ударением: нечто, некоторый, некогда.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (91,''Местоименные сочетания никто иной и ничто иное не связаны с противопоставлением и употребляются в предложениях, где есть отрицание.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (92,''НЕ пишется в случаях, когда не один (= много): Я был там не один раз.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (93,''НИ пишется в случаях, когда ни один (= никто): ни один ученик не захотел уходить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (94,''НЕ пишется раздельно, если словосочетание не кто иной (другой) включает частицу как'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (95,''Наречия пишутся слитно, если образованы от начинающихся с согласной прилагательных и местоимений с предлогами: вплотную, зачастую, вовсе.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (96,''Когда вследствие - предлог, пишется слитно и с Е на конце (вследствие). Имеет значение последствия чего-либо (вследствие пожара). Существительное следствие с предлогом В пишется раздельно и с буквой И на конце. Обычно между ними можно поставить слово (в этом следствии).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (97,''Предлоги пишутся раздельно, если это сочетание существительного с предлогом: иметь в виду, в виду города (поблизости, вблизи), в следствии участвовали, в роде существительного, на счет в банке.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (98,''Наречия пишутся через дефис, если: 1.образованы от порядковых числительных с приставкой во?, (в-): во-первых, в-третьих; 2.неопределенные с суффиксами -то, -либо, -нибудь и приставкой кое-(кой-), частицей –таки: когда-то, когда-либо, когда-нибудь, всё-таки.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (99,''В кратких формах причастия пишется -Н- (органичен, но ограничена, ограничено, ограничены).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (100,''Безударные гласные корня проверяются ударением:  соединить - едИный.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (101,''Написание непроверяемых безударных гласных проверяется по орфографическому словарю, например: диспетчер, оратор, инспектор, метеор, конструктор, директор, жаворонок, бактерия, абонент, абонемент, маркетинг, иждивенец и т.п.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (102,''Двойные согласные пишутся на стыке приставки и корня, если приставка кончается, а корень начинается одной и той же согласной: воззвание, подделать, раззадорить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (129,''Две буквы Н пишутся в прилагательных, образованных с помощью суффикса -н от сущ-го с основой на -н (сон-сонный, лимон-лимонный).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (130,''Две буквы Н пишутся в прилагательных, образованных от глаголов с помощью –ОВА-, -ЕВА-(ЁВА).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (131,''Двоеточие в бессоюзном предложении ставится, когда вторая часть поясняет предшествующую (перед второй частью можно поставить а именно).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (132,''Наречное выражение в крайнем случае не требует постановки знаков препинания. Но, если предположить, что есть пропуск слов нужно подписать, тире возможно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (206,''Правописание непроверяемых безударных гласных в корне слова нужно запоминать. Правильное написание оборачиваемость.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (104,''Прилагательное искусственный и существительное искусство, от которого оно образовано, являются словарными словами. Их написание необходимо запомнить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (105,''Точка с запятой между частями бессоюзного сложного предложения с перечислительными отношениями ставится в том случае, если эти части значительно распространены и имеют внутри себя запятые, например, при однородных членах, причастных или деепричастных оборотах, уточнениях и так далее:'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (106,''В сложном предложении, в котором более одной грамматической основы (то есть больше одного подлежащего и сказуемого), союз и является сочинительным и соединяет части сложносочиненного предложения, между которыми чаще всего ставится запятая, точка с запятой, тире или двоеточие.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (107,''Если причастный оборот стоит после слова, к которому относится (определяемого) или это относится к личному местоимению, то он выделяется с обеих сторон на письме запятыми. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (108,''Запятая ставится между однородными членами, связанными противительными союзами: а, но, да (= но), однако, хотя, зато и присоединительными союзами: а также, а то и.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (109,''Если при перечислении однородных членов есть повторяющиеся союзы, то запятая стаивится перед всеми однородными членами, кроме первого. При этом перед первым однородным членом союз может стоять, а может и нет.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (110,''Ставится запятая перед второй частью двойных союзов как… так и, не только… но и, не столько… сколько, хотя и…но, скорее… чем.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (111,''Сложноподчиненное предложение с придаточным времени требует постановки запятой. Второй знак ставится. т.к. есть грамматическая основа с составным глагольным сказуемым.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (112,''Запятая и тире в сложном предложении ставится одновременно. Запятая закрывает придаточную часть с союзомкоторые, тире ставится перед это.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (113,''При одновременном сочетании запятой и тире вначале ставится запятая, а затем тире. В данном случае тире нужно, чтобы показать, что придаточная часть относится ко всем однородным членам.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (114,''В этом сложноподчиненном предложении нужен знак препинания (придаточное времени).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (115,''Ввиду пишется слитно, когда является предлогом и его можно заменить на из-за. Существительное с предлогом (в качестве зависимого слова) в виду пишется раздельно. Устойчивое сочетание иметь в виду пишется раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (116,''Предлог насчет со значением  о, относительно чего-то пишется слитно. Существительное с предлогом на счет  пишется раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (117,''Предлог насчет со значением  о, относительно чего-то пишется слитно. Существительное с предлогом на счет  пишется раздельно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (118,''В данном случае в предложения однородные члены - знак не нужен.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (119,''Проработать и сформулировать - однородные члены предложения, соединенные союзом и. Запятая не нужна.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (120,''Перед поэтому ставится запятая, если она объединяет два и более простых предложения в сложной синтаксической конструкции, находясь на стыке частей.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (121,''В этом предложении не нужен знак препинания - одиночный разделительный союз или.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (122,''Между двумя сложными предложениями ставится запятая. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (123,''НЕ  пишется раздельно с глаголами и деепричастиями: не желая, не хватает (кроме случаев, когда слово без не не употребляется (негодуя, нездоровится).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (125,''Отрицательные и неопределенные местоимения образуются от вопросительных местоимений при помощи частиц не-, ни-. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (126,''Сложные прилагательные, образованные путём слияния словосочетания (наречие + прилагательное/причастие) в одно слово, пишутся слитно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (128,''Зачастую - нареч., пишется слитно. Образовано от однокоренного прилагательного с помощью приставки и суффикса. Не путать с однокоренным прилагательным за частую ограду.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (124,''Отрицательные и неопределенные местоимения образуются от вопросительных местоимений. В отрицательных местоимениях пишется: под ударением – не: некого обрадовать, нечего делать, некий прохожий; без ударения – ни: никакой работы, ничей карандаш.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (127,''Наречия, образованные соединением предлогов-приставок с полными именами прилагательнымими ,числительными, местоимениями, пишутся слитно: вкрутую (от прил-х), надвое (от числит-х, вовсе (от местоимений). Наречие вовсе обычно употребляется со следующей за этим словом отриц. частицей НЕ. При отсутствии отрицания не употребление вовсе носит разговорный характер.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (133,''Запятая закрыла оборот в сложноподчиненном предложении с союзом что. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (134,''Вторая часть предложения раскрывает смысл первой, нужно двоеточие.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (135,''Русь моя - обращение, ставится запятая.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (136,''Две буквы Н пишутся в причастиях ,образованных от глагола (авторизовать,) с помощью  суффикса -ОВАНН.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (137,''Две буквы Н пишутся в причастиях, образованных от глаголов сов. в, с помощью суффикса ЕНН.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (186,''Приставка под- пишется одинаково, хотя произносится по-разному: под-, пот-, пад-, пат-.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (139,''Запятая закрывает придаточную часть (…. от главной цели). Тире ставится перед словом «это».'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (140,''Запятая ставится перед союзом «что», в данном предложении закрывает оборот. Перед «а» ставится.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (187,''Для проверки необходимо подобрать проверочное слово весть. Следует писать неизвестно.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (142,''Обособлена придаточная часть сложноподчиненного предложения с союзом которых.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (143,''Причастный оборот обособляется запятыми.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (144,''Некорректно пишем слитно, т.к. можно найти синоним (ошибочно, неправильно),  нет противопоставления с союзом а, нет конструкций далеко не, отнюдь не, вовсе не.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (145,''Причастный оборот выделяется запятыми, если определяемое слово стоит впереди оборота'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (146,''Запятая ставится при наличии однородных членов предложения (перечислении): размещал, заходил.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (147,''Запятая ставится в сложноподчиненном предложении перед союзом что. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (148,''Числительные триста и четыреста пишутся с а на конце.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (149,''Особое склонение имеют числительные двести, триста, четыреста (д.п. двумстам).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (150,''Особое склонение имеют числительные двести, триста, четыреста (д.п. трёмстам).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (151,''Особое склонение имеют числительные двести, триста, четыреста (т.п. четырьмястами).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (189,''Тире между подлежащим и сказуемым ставится, если оба главных члена выражены именами существительными в именительном падеже разработка - задание.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (196,''Для проверки безударной гласной в корне слова подбирается проверочное, в котором сомнительная гласная займет ударную позицию. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (154,''Сложное прилагательное пишется слитно, если между словами, из которых оно образовано, нельзя поставить союз и, например: семилетний (семь лет). Возможно, вы написали предложение с маленькой буквы.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (155,''Составные и дробные числительные пишуся раздельно: сорок пять, сто сорок шесть. Возможно, вы написали предложение с маленькой буквы.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (156,''Не склоняются: иноязычные и русские фамилии на согласный, если относятся к женщинам; мужские и женские фамилии на –ово,-ако, -аго,-яго. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (157,''Не склоняются иностранные фамилии на гласный: роман Эмиля Золя, пьеса Бернарда Шоу.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (158,''Склоняются мужские фамилии на согласный.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (159,''Все женские фамилии на согласный не склоняются.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (160,''Мужские фамилии на -ь,й  склоняются как существительные  2-го склонения м.р. Женские фамилии на -ь,-й не склоняются.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (161,''Мужские фамилии на согласный склоняются как сущ-е 2 склонения.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (162,''Для правильного написания слова с непроизносимым согласным. нужно изменить его форму или подобрать родственное слово таким образом, чтобы в нем после первого или второго согласного стоял гласный звук (участие).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (163,''Отрицательные и неопределенные местоимения образуются от относительных местоимений при помощи частиц. В отрицательных местоимениях пишется: под ударением – не: некого обрадовать, нечего делать, некий прохожий; без ударения – ни: никакой работы, ничей карандаш.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (164,''Слово является словарным. Его написание следует запомнить.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (165,''Пребывание (=нахождение), значит пишется приставка пре-'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (167,''Если после корня -ЛАГ-, -ЛОЖ- стоит суффикс -А-, то в корне пишется А, если этого суффикса нет, то буква О.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (168,''Если глагол отвечает на вопрос что делает?, что сделает?, то пишется сочетание -тся (без мягкого знака), если на вопрос что делать?, что сделать?, то -ться (с мягким знаком).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (169,''Если после корня -БЕР-, -БИР- стоит суффикс -А-, то в корне пишется И, если этого суффикса нет, то буква Е.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (170,''УрОвнять значит сделать ровным, урАвнять, значит сделать равным.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (171,''В конце наречия снова пишется суффикс -а, так как в его составе имеется приставка с-. '')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (172,''НИ пишется слитно с отриц местоимениями, если между частицей и местоимением нет предлога.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (173,''Пишем слитно, так как можно заменить синонимом без частицы не (сомнительный, маловероятный).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (174,''Краткое причастие, обозначает признак по действию. Пишется с одной Н.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (175,''Не пишется слитно с наречиями на -о , которые при ее добавлении получают противоположный смысл достаточно - недостаточно (мало).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (176,''Комиссионный от словарного слова комиссия (написание двойной буквы с нужно запомнить).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (178,''У существительных первого склонения окончание И пишется в родительном падеже, а окончание Е в дательном и предложном падежах, единственном числе.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (179,''Существительное м.р., ед.ч., 2 склонение с нулевым окончанием в предложном падеже пишем окончание -Е.'')';
