  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (181,''Прилагательное образовано от существительного интерес. Проверяем, изменив форму из полной в краткую: интересный - интересен. Буквы Т нет.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (182,''Запятая ставится перед союзом НО. Если предложение начинается с НО, то запятой нет.'')';
