  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (41,''Не с причастиями пишется раздельно, если есть усиление отрицания (кроме зависимых слов).'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (46,''Сложное прилагательное пишется слитно, если между словами, из которых оно образовано, нельзя поставить союз и, между ними есть соединительная гласная о (например, народнохозяйственный – народное хозяйство)'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (188,''Для проверки безударной гласной в корне слова подбирается проверочное, в котором сомнительная гласная займет ударную позицию.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (67,''При склонении сложных числительных обозначающих сотни и десятки и заканчивающихся на –сот или –десят в именительном падеже, каждая часть склоняется, как простое числительное: пятидесяти, девятисот, семьюдесятью, и т.д.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (190,''Частица НИ используется для усиления отрицания либо утверждения, в данном случае следует писать никого.'')';
  EXECUTE IMMEDIATE 'INSERT INTO RULE (rule_ID, text) VALUES (191,''Частица НИ используется для усиления отрицания либо утверждения, в данном случае следует писать Нелёгкое.'')';
